class Topology
  constructor: (ele) ->
    typeof (ele) is "string" and (ele = document.getElementById(ele))
    w = ele.clientWidth
    h = ele.clientHeight
    self = this
    @force = d3.layout.force().gravity(.05).distance(200).charge(-800).size([w, h])
    @nodes = @force.nodes()
    @links = @force.links()
    @clickFn = ->

    @vis = d3.select(ele).append("svg:svg").attr("width", w).attr("height", h).attr("pointer-events", "all")
    @force.on "tick", (x) ->
      self.vis.selectAll("g.node").attr "transform", (d) ->
        "translate(" + d.x + "," + d.y + ")"

      self.vis.selectAll("line.link").attr("x1", (d) ->
        d.source.x
      ).attr("y1", (d) ->
        d.source.y
      ).attr("x2", (d) ->
        d.target.x
      ).attr "y2", (d) ->
        d.target.y
  doZoom: ->
    d3.select(this).select("g").attr "transform", "translate(" + d3.event.translate + ")" + " scale(" + d3.event.scale + ")"
  addNode:(node) ->
    @nodes.push node
  #增加节点
  addNodes:(nodes) ->
    if Object::toString.call(nodes) is "[object Array]"
      self = this
      nodes.forEach (node) ->
        self.addNode node
  #增加连线
  addLink : (source, target) ->
    @links.push
      source: @findNode(source)
      target: @findNode(target)
  #增加多个连线
  addLinks : (links) ->
    if Object::toString.call(links) is "[object Array]"
      self = this
      links.forEach (link) ->
        self.addLink link![pic]("source"), link![pic]("target")
  #删除节点
  removeNode : (id) ->
    i = 0
    n = @findNode(id)
    links = @links
    (if links![pic](i)["source"] is n or links![pic](i)["target"] is n then links.splice(i, 1) else ++i)  while i < links.length
    @nodes.splice @findNodeIndex(id), 1
  #删除节点下的子节点，同时清除link信息
  removeChildNodes : (id) ->
    node = @findNode(id)
    nodes = @nodes
    links = @links
    self = this

    linksToDelete = []
    childNodes = []
    links.forEach (link, index) ->
      link![pic]("source") is node and linksToDelete.push(index) and childNodes.push(link![pic]("target"))

    linksToDelete.reverse().forEach (index) ->
      links.splice index, 1

    remove = (node) ->
      length = links.length
      i = length - 1

      while i >= 0
        if links![pic](i)["source"] is node
          target = links![pic](i)![pic]("target")
          links.splice i, 1
          nodes.splice self.findNodeIndex(node.id), 1
          remove target
        i--

    childNodes.forEach (node) ->
      remove node

    i = nodes.length - 1

    #清除没有连线的节点
    while i >= 0
      haveFoundNode = false
      j = 0
      l = links.length

      while j < l
        (links![pic](j)["source"] is nodes![pic](i) or links![pic](j)["target"] is nodes![pic](i)) and (haveFoundNode = true)
        j++
      not haveFoundNode and nodes.splice(i, 1)
      i--
  #查找节点
  findNode : (id) ->
    nodes = @nodes
    for i of nodes
      return nodes![pic](i)  if nodes![pic](i)["id"] is id
    null
  #查找节点所在索引号
  findNodeIndex : (id) ->
    nodes = @nodes
    for i of nodes
      return i  if nodes![pic](i)["id"] is id
    -1
  #节点点击事件
  setNodeClickFn : (callback) ->
    @clickFn = callback
  #更新拓扑图状态信息
  update : ->
    link = @vis.selectAll("line.link").data(@links, (d) ->
      d.source.id + "-" + d.target.id
    ).attr("class", (d) ->
      (if d![pic]("source")["status"] and d![pic]("target")["status"] then "link" else "link link_error")
    )
    link.enter().insert("svg:line", "g.node").attr "class", (d) ->
      (if d![pic]("source")["status"] and d![pic]("target")["status"] then "link" else "link link_error")

    link.exit().remove()
    node = @vis.selectAll("g.node").data(@nodes, (d) ->
      d.id
    )
    nodeEnter = node.enter().append("svg:g").attr("class", "node").call(@force.drag)
    self = this
    #增加图片，可以根据需要来修改
    #根据类型来使用图片
    nodeEnter.append("svg:image").attr("class", "circle").attr("xlink:href", (d) ->
      (if d.expand then "http://ww2.sinaimg.cn/large/412e82dbjw1dsbny7igx2j.jpg" else "http://ww4.sinaimg.cn/large/412e82dbjw1dsbnxezrrpj.jpg")
    ).attr("x", "-32px").attr("y", "-32px").attr("width", "64px").attr("height", "64px").on "click", (d) ->
